mapper.select:
SELECT * FROM address_search_cache 
WHERE city_name = #{cityName} 
AND keyword = #{keyword} 
AND source = #{source}
AND (expire_time IS NULL OR expire_time > NOW())

mapper.update:
UPDATE address_search_cache 
SET results = #{results},
    hit_count = #{hitCount},
    last_hit_time = #{lastHitTime},
    expire_time = #{expireTime}
WHERE id = #{id}

mapper.delete:
DELETE FROM address_search_cache 
WHERE city_name = #{cityName} 
AND keyword = #{keyword} 
AND source = #{source}
